const loader=document.querySelector(".loader");const main=document.querySelector("#main");function fadeLoadingAnimation(){let fadeOutTime=200;let delayTime=1500;let doesCookieExist=!1
    if(checkCookie('port-page')){doesCookieExist=!0}else{setCookie('port-page','true',1)}
    if(window.scrollY===0&&!doesCookieExist){fadeOutTime=200;delayTime=1500}else{fadeOutTime=0;delayTime=0}
    setAOSDelay(delayTime);setTimeout(()=>{$(".loader").fadeOut(fadeOutTime);document.body.classList.remove("no-scroll");document.querySelector("#burger-btn").style.opacity="1"},delayTime)}
    const setAOSDelay=(delayAmount)=>{const elementsWithDelay=document.querySelectorAll('[data-aos-delay]');elementsWithDelay.forEach((element)=>{element.setAttribute("data-aos-delay",delayAmount)})};function setCookie(name,value,hours){const date=new Date();date.setTime(date.getTime()+(hours*60*60*1000));const expires="expires="+date.toUTCString();document.cookie=`${name}=${value}; ${expires}; path=/`}
    function checkCookie(name){const cookies=document.cookie.split(';');for(let i=0;i<cookies.length;i++){const cookie=cookies[i].trim();if(cookie.startsWith(name+'=')){return!0}}
    return!1}
    fadeLoadingAnimation()